const onKeyDown = function ( event ) {

    switch ( event.code ) {

        case 'ArrowUp':
        case 'KeyW':
            
            break;

        case 'ArrowLeft':
        case 'KeyA':
            
            break;

        case 'ArrowDown':
        case 'KeyS':
            
            break;

        case 'ArrowRight':
        case 'KeyD':
            
            break;

        case 'Space':
            
            break;

    }

};

